import SwiftUI

struct FMCControlView: View {
    let device: WinwingDevice
    @State private var backlight: Double = 0
    @State private var screenBacklight: Double = 0
    @State private var overallLedsBrightness: Double = 0
    
    // LED states for FMC indicators
    @State private var ledStates: [Bool] = Array(repeating: false, count: 17)
    @State private var selectedTestPage: String = "TOLISS_INIT";
    @State private var selectedFont: FMCWrapper.FontType = .airbusVariant1
    
    private let indicatorLEDs: [(id: Int, name: String)] = [
        (8, "FAIL"), (9, "FM"), (10, "MCDU"), (11, "MENU"), 
        (12, "FM1"), (13, "IND"), (14, "RDY"), (15, "STATUS"), (16, "FM2")
    ]
    
    var body: some View {
        VStack(alignment: .leading, spacing: 20) {
            Text("FMC Controls")
                .font(.headline)
                .padding(.top, 8)
            
            // Backlight Controls
            VStack(alignment: .leading, spacing: 12) {
                Text("Backlights")
                    .font(.subheadline)
                    .fontWeight(.medium)
                
                HStack(alignment: .center, spacing: 16) {
                    Text("Backlight")
                        .frame(width: 100, alignment: .leading)
                    Slider(value: $backlight, in: 0...255, step: 1)
                        .frame(width: 160)
                    Text("\(Int(backlight))")
                        .frame(width: 36, alignment: .trailing)
                    Button(action: { setBacklight() }) {
                        Text("Set")
                    }
                    .buttonStyle(.bordered)
                }
                
                HStack(alignment: .center, spacing: 16) {
                    Text("Screen Backlight")
                        .frame(width: 100, alignment: .leading)
                    Slider(value: $screenBacklight, in: 0...255, step: 1)
                        .frame(width: 160)
                    Text("\(Int(screenBacklight))")
                        .frame(width: 36, alignment: .trailing)
                    Button(action: { setScreenBacklight() }) {
                        Text("Set")
                    }
                    .buttonStyle(.bordered)
                }
                
                HStack(alignment: .center, spacing: 16) {
                    Text("Overall LEDs")
                        .frame(width: 100, alignment: .leading)
                    Slider(value: $overallLedsBrightness, in: 0...255, step: 1)
                        .frame(width: 160)
                    Text("\(Int(overallLedsBrightness))")
                        .frame(width: 36, alignment: .trailing)
                    Button(action: { setOverallLedsBrightness() }) {
                        Text("Set")
                    }
                    .buttonStyle(.bordered)
                }
            }
            
            Divider()
                .padding(.vertical, 8)
            
            // LED Controls
            VStack(alignment: .leading, spacing: 12) {
                Text("Indicator LEDs")
                    .font(.subheadline)
                    .fontWeight(.medium)
                
                LazyVGrid(columns: Array(repeating: GridItem(.flexible()), count: 3), spacing: 8) {
                    ForEach(indicatorLEDs, id: \.id) { led in
                        Toggle(led.name, isOn: Binding(
                            get: { ledStates[led.id] },
                            set: { value in
                                ledStates[led.id] = value
                                setLED(led.id, state: value)
                            }
                        ))
                        .toggleStyle(.switch)
                        .controlSize(.mini)
                    }
                }
                
                HStack {
                    Button("All On") {
                        for led in indicatorLEDs {
                            ledStates[led.id] = true
                            setLED(led.id, state: true)
                        }
                    }
                    .buttonStyle(.bordered)
                    
                    Button("All Off") {
                        for led in indicatorLEDs {
                            ledStates[led.id] = false
                            setLED(led.id, state: false)
                        }
                    }
                    .buttonStyle(.bordered)
                    
                    Spacer()
                }
                .padding(.top, 8)
            }
            
            Divider()
                .padding(.vertical, 8)
            
            // Display Controls
            VStack(alignment: .leading, spacing: 12) {
                Text("Display Controls")
                    .font(.subheadline)
                    .fontWeight(.medium)
                
                HStack {
                    Button("Clear Display") {
                        clearDisplay()
                    }
                    .buttonStyle(.bordered)
                    
                    Spacer()
                }
                
                // Font selection
                VStack(alignment: .leading, spacing: 8) {
                    Text("Font selection")
                        .font(.subheadline)
                        .fontWeight(.medium)
                    
                    HStack {
                        Picker("Font", selection: $selectedFont) {
                            ForEach(FMCWrapper.FontType.allCases, id: \.self) { font in
                                Text(font.displayName).tag(font)
                            }
                        }
                        .pickerStyle(.menu)
                        .frame(width: 150)
                        
                        Button("Set Font") {
                            setFont()
                        }
                        .buttonStyle(.bordered)
                        
                        Spacer()
                    }
                }
                
                // Test page selection
                VStack(alignment: .leading, spacing: 8) {
                    Text("Test pages")
                        .font(.subheadline)
                        .fontWeight(.medium)
                    Text("Fonts for the test pages are not automatically updated.\nThis is by design. In the real world, loading an aircraft profile will load the appropriate font.")
                        .font(.caption)
                        .foregroundColor(.secondary)
                        .lineLimit(nil)
                        .fixedSize(horizontal: false, vertical: true)
                        .frame(maxHeight: .infinity, alignment: .top)
                    
                    Picker("Test page", selection: $selectedTestPage) {
                        Text("TOLISS_INIT").tag("TOLISS_INIT")
                        Text("TOLISS_INIT_EMPTY").tag("TOLISS_INIT_EMPTY")
                        Text("TOLISS_TAKEOFF").tag("TOLISS_TAKEOFF")
                        Text("TOLISS_DIRTO").tag("TOLISS_DIRTO")
                        Text("──────────").disabled(true)
                        Text("ZIBO_PERF_INIT").tag("ZIBO_PERF_INIT")
                        Text("ZIBO_FLT_PLN").tag("ZIBO_FLT_PLN")
                        Text("──────────").disabled(true)
                        Text("IXEG_PERF_INIT").tag("IXEG_PERF_INIT")
                        Text("IXEG_ROUTE").tag("IXEG_ROUTE")
                        Text("──────────").disabled(true)
                        Text("LAMINAR_A333_DATA_INDEX").tag("LAMINAR_A333_DATA_INDEX")
                        Text("──────────").disabled(true)
                        Text("XCRAFTS_RADIO").tag("XCRAFTS_RADIO")
                        Text("XCRAFTS_PROGRESS").tag("XCRAFTS_PROGRESS")
                        Text("XCRAFTS_DEPT_SID").tag("XCRAFTS_DEPT_SID")
                        Text("──────────").disabled(true)
                        Text("CHARACTER_TEST").tag("CHARACTER_TEST")
                        Text("ALL_GLYPHS").tag("ALL_GLYPHS")
                    }
                    .pickerStyle(.menu)
                    .frame(width: 200)
                    
                    Button("Show Test Page") {
                        showTestPage()
                    }
                    .buttonStyle(.bordered)
                }
            }
            
            Spacer()
        }
        .padding()
        .onAppear(perform: viewDidAppear)
    }
    
    // MARK: - Control Functions
    
    private func viewDidAppear() {
        device.update()
    }
    
    private func setBacklight() {
        guard let fmc = device.fmc else { return }
        fmc.setBacklight(UInt8(backlight))
    }
    
    private func setScreenBacklight() {
        guard let fmc = device.fmc else { return }
        fmc.setScreenBacklight(UInt8(screenBacklight))
    }
    
    private func setOverallLedsBrightness() {
        guard let fmc = device.fmc else { return }
        fmc.setOverallLedsBrightness(UInt8(overallLedsBrightness))
    }
    
    private func setLED(_ ledId: Int, state: Bool) {
        guard let fmc = device.fmc else { return }
        fmc.setLed(ledId, state: state)
    }
    
    private func clearDisplay() {
        guard let fmc = device.fmc else { return }
        fmc.clearDisplay()
    }
    
    private func setFont() {
        guard let fmc = device.fmc else { return }
        fmc.setFont(selectedFont)
        showTestPage()
    }
    
